int gcd(int a, int b)
{
    return (b != 0) ? gcd(b,a%b) : a;
}
int main(void)
{
    int s2 = 1000 / 2;
    int mlimit = static_cast<int>( ceil(sqrt(s2)) ) - 1;
    for(int m = 2; m <= mlimit; ++m)
        if(s2 % m == 0)
        {
            int sm = s2 / m;
            while(sm % 2 == 0)
                sm = sm / 2;
            int k;
            if(m % 2 == 1)
                k = m + 2;
            else
                k = m + 1;
            while( k < 2 * m && k <= sm )
            {
                if(sm % k == 0 && gcd(k, m) == 1)
                {
                    int d = s2 / (k*m);
                    int n = k - m;
                    int a = d * (m * m - n * n);
                    int b = 2 * d * m * n;
                    int c = d * (m * m + n * n);
                    printf("%d %d %d %d",a,b,c,a*b*c);
                    return 0;
                }
                k += 2;
            }
        }
    return 0;
}
