void sieve(vector<int>& v, const int upper)
{
    bool *num = new bool[static_cast<unsigned long long>(upper)];
    memset(num, false, sizeof(bool) * upper);
    for(int i = 2; i <= upper; ++i)
    {
        if(num[i] == false)
        {
            v.push_back(i);
            int p = 2;
            while(i * p < upper)
            {
                num[i*p] = true;
                ++p;
            }
        }
    }
    delete[] num;
}
int main(void)
{
    vector<int> v;
    const int upper = 2e6;
    sieve(v,upper+1);
    long long sum = 0;
    for(auto i : v)
        sum += i;
    cout << sum << endl;
    return 0;
}
```
