const int TARGET = 1e6;
const int RANGE = 10;
int getPro(int n)
{
    int ans = 1;
    for(int i = n; i > 1; --i)
        ans *= i;
    return ans;
}
list<int> num;
void dfs(int target, int p, queue<int> &s)
{
    if(p == 1)
    {
        s.push(*num.begin());
        return ;
    }
    int part = getPro(p-1);
    cout << "part" << part << endl;
    auto i = num.begin();
    for(;i != num.end(); ++i)
        if(target <= part)
        {
            cout << *i << "b" << endl;
            break;
        }
        else
            target -= part;
    s.push(*i);
    num.erase(i);
    dfs(target, p-1,s);
}

int main(void)
{
    queue<int> s;
    for(int i = 0; i < RANGE; ++i)
        num.push_back(i);
    dfs(TARGET, RANGE, s);
    long long ans = 0;
    while(!s.empty())
    {
        cout << s.front() << "xxx" << endl;
        ans = ans * 10 + s.front();
        s.pop();
    }
    cout << setfill('0') << setw(10) << ans << endl;
    return 0;
}
