class BigInt
{
public:
    BigInt(int num);
    BigInt(const BigInt&);
    BigInt(BigInt&& o) noexcept;
    BigInt& operator = (const BigInt&);
    static BigInt Zero();
    static BigInt One();

    bool operator == (const BigInt&) const;
    BigInt& operator ++(); //++i
    BigInt& operator --(); //--i
    BigInt operator +(const BigInt&);
    BigInt operator +=(const BigInt&);
    BigInt operator *(const BigInt&);
    BigInt operator *=(const BigInt&);

    inline size_t& size();
    inline size_t size() const;
    string toString() const;
    ~BigInt();

    friend ostream& operator << (ostream& out, const BigInt& o)
    {
        auto ptr = o.m_num + o.size();
        do{
            --ptr;
            out << *ptr;
        }while(ptr != o.m_num);
        return out;
    }
private:
    BigInt();
    int* m_num;
    size_t m_size;
    void format();
};
size_t& BigInt::size()
{
    return m_size;
}
size_t BigInt::size() const
{
    return m_size;
}
BigInt::BigInt()
{
}
BigInt::~BigInt()
{
    if(m_num != nullptr)
    delete[] m_num;
    m_size = 0;
}
BigInt::BigInt(int num)
{
    size_t cnt = 0;
    {
        int tmp = num;
        do{
            ++cnt;
            tmp /= 10;
        }while(tmp != 0);
    }
    size() = cnt;
    m_num = new int[size()];
    auto ptr = m_num;
    do{
        *ptr = num % 10;
        num /= 10;
        ++ptr;
    }while(num != 0);
}
BigInt::BigInt(const BigInt& o)
{
    size() = o.size();
    m_num = new int[size()];
    memcpy(m_num, o.m_num, size() * sizeof(int));
}
BigInt::BigInt(BigInt&& o) noexcept
{
    size() = o.size();
    m_num = o.m_num;
    //delete;
    o.size() = 0;
    o.m_num = nullptr;
}
BigInt& BigInt::operator = (const BigInt& o)
{
    size() = o.size();
    if(m_num != nullptr)
        delete [] m_num;
    m_num = new int[size()];
    memcpy(m_num, o.m_num, size() * sizeof(int));
    return *this;
}
BigInt BigInt::Zero()
{
    BigInt a(0);
    return a;
}
BigInt BigInt::One()
{
    BigInt a(1);
    return a;
}
bool BigInt::operator==(const BigInt& o) const
{
    if(size() != o.size())
        return false;
    for(size_t i = 0; i < size(); ++i)
        if(m_num[i] != o.m_num[i])
            return false;
    return true;
}
BigInt& BigInt::operator ++()
{
    ++m_num[0];
    format();
    return *this;
}
BigInt& BigInt::operator --()
{
    --m_num[0];
    format();
    return *this;
}
void BigInt::format()
{
    // Carry
    for(size_t i = 0; i < size() - 1; ++i)
        if(m_num[i] >= 10)
        {
            m_num[i+1] += m_num[i] / 10;
            m_num[i] %= 10;
        }
        else
            if(m_num[i] < 0)
            {
                int cnt = (m_num[i] / 10 + 1);
                m_num[i] += cnt * 10;
                m_num[i+1] -= cnt;
            }
    // Stretch out digital
    if(m_num[size() - 1] >= 10)
    {
        int* new_num = new int[size() + 1];
        memcpy(new_num, m_num, size() * sizeof(int));
        new_num[size()] = new_num[size() - 1] / 10;
        new_num[size() - 1] %= 10;
        ++size();
        if(m_num != nullptr)
            delete [] m_num;
        m_num = new_num;
    }
    // Draw back digital
    if(size() == 1)
        return;
    auto ptr = m_num + size();
    do{
        --ptr;
        if(*ptr != 0)
            break;
    }while(ptr != m_num);
    size_t new_size = static_cast<size_t>(ptr - m_num + 1);
    if(size() != new_size)
    {
        int* new_num = new int[new_size];
        memcpy(new_num, m_num, new_size * sizeof(int));
        if(m_num != nullptr)
            delete [] m_num;
        m_num = new_num;
        size() = new_size;
    }
}
BigInt BigInt::operator +(const BigInt& o)
{
    BigInt ans;
    size_t length = max(size(), o.size());
    ans.m_num = new int[length];
    ans.size() = length;
    for(size_t i = 0; i < min(size(), o.size()); ++i)
        ans.m_num[i] = m_num[i] + o.m_num[i];
    if(size() > o.size())
        for(size_t i = o.size(); i < size(); ++i)
            ans.m_num[i] = m_num[i];
    else
        for(size_t i = size(); i < o.size(); ++i)
            ans.m_num[i] = o.m_num[i];
    ans.format();
    return ans;
}
BigInt BigInt::operator+=(const BigInt& o)
{
    if(size() < o.size())
    {
        int* new_num = new int[o.size()];
        memcpy(new_num,m_num,size());
        for(size_t i = size(); i < o.size(); ++i)
            new_num[i] = 0;
        delete [] m_num;
        m_num = new_num;
    }
    for(size_t i = 0; i < o.size(); ++i)
        m_num[i] += o.m_num[i];
    format();
    return *this;
}
BigInt BigInt::operator *=(const BigInt& o)
{
    size_t new_size = size() + o.size() - 1;
    int* new_num = new int[new_size];
    memset(new_num, 0, new_size * sizeof(int));
    for(size_t i = 0; i < size(); ++i)
        for(size_t j = 0; j < o.size(); ++j)
            new_num[i + j] += m_num[i] * o.m_num[j];
    size() = new_size;
    delete [] m_num;
    m_num = new_num;
    format();
    return *this;
}
BigInt BigInt::operator *(const BigInt& o)
{
    BigInt ans;
    size() = size() + o.size() - 1;
    ans.m_num = new int[size()];
    memset(ans.m_num, 0, size() * sizeof(int));
    for(size_t i = 0; i < size(); ++i)
        for(size_t j = 0; j < o.size(); ++j)
            ans.m_num[i + j] += m_num[i] * o.m_num[j];
    ans.format();
    return ans;
}
string BigInt::toString() const
{
    char* str = new char[size() + 1];
    for(size_t i = 0; i < size(); ++i)
        str[size() - i - 1] = static_cast<char>(m_num[i] + '0');
    str[size()] = '\0';
    return string(str);
}
BigInt mulpow(BigInt base, int index)
{
    BigInt ans = BigInt::One();
    while(index != 0)
    {
        if(index & 1)
            ans *= base;
        index >>= 1;
        base *= base;
    }
    return ans;
}
int main()
{
