        for(int j = 0; j < 20 - 4; ++j)
        {
            tmp = 1;
            for(int k = 0; k < 4; ++k)
                tmp *= maps[i+k][j+k];
            ans = max(ans,tmp);
        }
    // diagonal2

    for(int i = 3; i < 20; ++i)
        for(int j = 0; j < 20 - 4; ++j)
        {
            tmp = 1;
            for(int k = 0; k < 4; ++k)
                tmp *= maps[i-k][j+k];
            ans = max(ans,tmp);
        }

    cout << ans << endl;
    return 0;
}
