bool isaccepted(long long num)
{
    int factor_count = 0;
    long long upper = static_cast<long long>(sqrt(num));
    for(int i = 2; i < upper; ++i)
        if(num % i == 0)
            factor_count += 2;
    if(upper * upper == num)
        factor_count += 1;
    if(factor_count >= 500)
        return true;
    else
        return false;
}
int main(void)
{
    int ans = 1;
    int i = 1;
    while(!isaccepted(ans))
        ans += ++i;
    cout << ans << endl;
    return 0;
}

