class BigInt
{
public:
    const size_t SIZE = 60;

    BigInt();
    ~BigInt();
    BigInt(int num);
    BigInt(BigInt&& b);
    BigInt(const BigInt& b);
    BigInt& operator = (const BigInt&);
    bool operator == (const BigInt& b);
    void operator += (const BigInt& b);
    string toString();
    friend istream& operator >> (istream& in, BigInt& b)
    {
        string str;
        in >> str;
        auto p = str.rbegin();
        size_t postion = 0;
        while(p != str.rend())
        {
            b.m_num[postion] = (*p) - '0';
            ++postion;
            ++p;
        }
        return in;
    }
    friend ostream& operator <<(ostream& out, const BigInt& b)
    {
        size_t postion = b.SIZE - 1;
        while(b.m_num[postion] == 0 && postion != 0)
            --postion;
        while(postion != 0)
        {
            out << static_cast<int>(b.m_num[postion]);
            --postion;
        }
        out << static_cast<int>(b.m_num[0]);
        return out;
    }
private:
    char* m_num;
};

BigInt::BigInt()
{
    m_num = new char[SIZE];
    memset(m_num, 0, sizeof(char) * SIZE);
}

BigInt::~BigInt()
{
    if(m_num != nullptr)
        delete[] m_num;
}
BigInt::BigInt(int num)
{
    assert(num >= 0);
    m_num = new char[SIZE];
    memset(m_num, 0, sizeof(char) * SIZE);
    int p = 0;
    do{
        m_num[p] = num % 10;
        num /= 10;
        p++;
    }while(num != 0);
}
BigInt::BigInt(BigInt&& b)
{
    m_num = b.m_num;
    b.m_num = nullptr;
}
BigInt::BigInt(const BigInt& b)
{
    this->m_num = b.m_num;
}
bool
BigInt::operator==(const BigInt& b)
{
    for(size_t i = 0;i < SIZE; ++i)
        if(m_num[i] != b.m_num[i])
            return false;
    return true;
}
void
BigInt::operator+=(const BigInt& b)
{
    for(size_t i = 0; i < SIZE; ++i)
        m_num[i] += b.m_num[i];
    for(size_t i = 0; i < SIZE - 1; ++i)
    {
        m_num[i + 1] += m_num[i] / 10;
        m_num[i] = m_num[i] % 10;
    }
}
string
BigInt::toString()
{
    size_t postion = SIZE - 1;
    while(m_num[postion] == 0 && postion != 0)
        --postion;
    string str(postion + 1, '\0');
    auto p = str.begin();
    while(postion != 0)
    {
        (*p) = m_num[postion] + '0';
        --postion;
        ++p;
    }
    (*p) = m_num[postion] + '0';
    return str;
}
int main(void)
{
    BigInt sum,tmp;
    while(cin >> tmp)
        sum += tmp;
    cout << sum.toString().substr(0,10) << endl;
    return 0;
}
