const int MAXN = 1e6;
int num[MAXN+2];
int main(void)
{
    memset(num,0,sizeof(num));
    num[1] = 1;
    int ans = 1;
    stack<long long> s;
    for(int i = 1; i <= MAXN; ++i)
    {
        if(num[i] == 0)
        {
            s.push(i);
            long long tmp = i;
            int count;
            while(tmp != 1)
            {
                tmp = (tmp % 2) ? (3 * tmp + 1) : (tmp / 2);
                if(tmp <= MAXN && num[tmp] != 0)
                {
                    count = num[tmp];
                    while(!s.empty())
                    {
                        ++count;
                        if(s.top() <= MAXN)
                            num[s.top()] = count;
                        s.pop();
                    }
                    break;
                }
                s.push(tmp);
            }
        }
        if(num[ans] < num[i])
            ans = i;
    }
    cout << ans << endl;
    return 0;
}
