const int MAXN = 21;
unsigned long long dp[MAXN][MAXN];
int main(void)
{
    dp[0][0] = 1;
    // init
    for(int i = 1; i < MAXN; ++i)
    {
        dp[i][0] = dp[i-1][0];
        dp[0][i] = dp[0][i-1];
    }
    for(int i = 1; i < MAXN; ++i)
        for(int j = 1; j < MAXN; ++j)
            dp[i][j] = dp[i-1][j] + dp[i][j-1];
